        sessionId: data.session_id,
        userId: data.user_id || 'unknown',
        userName: data.user_name || 'Guest',
        configName: data.config_name || 'unknown',
        timestamp: data.last_activity,
        sessionStartTime: data.session_start,
        lastActivityTime: data.last_activity,
        sessionDuration: parseInt(data.duration) || 0,
        messageCount: parseInt(data.messages) || 0,
        messageTimestamps: messageTimestamps,
        currentLanguage: data.language || 'en',
        userAgent: data.user_agent || '',
