// ğŸ” Security Check: API KEY + RATE LIMITING
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const config = $('âš™ï¸ CONFIG: Detect').first().json;
const API_KEY = config.api_key;
const RATE_LIMIT_WINDOW_MS = config.rate_limit_window_ms;
const RATE_LIMIT_MAX_REQUESTS = config.rate_limit_max_requests;

const inputData = $('ğŸŒ Webhook: Detect').item.json;
const body = inputData.body || {};
const headers = inputData.headers || {};
const query = inputData.query || {};
const clientIP = headers['x-real-ip'] || headers['x-forwarded-for'] || headers['cf-connecting-ip'] || 'unknown';
const now = Date.now();

const staticData = $getWorkflowStaticData('global');
if (!staticData.rateLimits) { staticData.rateLimits = {}; }
for (const ip in staticData.rateLimits) {
  if (now - staticData.rateLimits[ip].firstRequest > RATE_LIMIT_WINDOW_MS) { delete staticData.rateLimits[ip]; }
}
if (!staticData.rateLimits[clientIP]) {
  staticData.rateLimits[clientIP] = { count: 1, firstRequest: now };
} else {
  staticData.rateLimits[clientIP].count++;
  if (staticData.rateLimits[clientIP].count > RATE_LIMIT_MAX_REQUESTS) {
    return [{ json: { ...inputData, authorized: false, error: { success: false, error: 'Too many requests', details: `Limit: ${RATE_LIMIT_MAX_REQUESTS} requests per minute`, status: 429 } } }];
  }
}

const providedKey = headers['x-api-key'] || body.apiKey || query.apiKey || '';
if (!providedKey) { return [{ json: { ...inputData, authorized: false, error: { success: false, error: 'API key not provided', status: 401 } } }]; }
if (providedKey !== API_KEY) { return [{ json: { ...inputData, authorized: false, error: { success: false, error: 'Invalid API key', status: 401 } } }]; }

return [{ json: { ...inputData, authorized: true } }];

