const requestData = items[0].json.body || items[0].json;

const sessionId = requestData.sessionId;
const userName = requestData.userName || 'Guest';
const language = requestData.language || 'en';

if (!sessionId) {
    throw new Error('sessionId is required');
}

return [{
    json: {
        sessionId: sessionId,
        userName: userName,
        language: language
    }
