WITH all_chats AS (
    SELECT id, session_id, message::json->>'type' as message_type, message::json->>'content' as content, created_at as timestamp, platform, 'ru' as language
    FROM {{ $('âš™ï¸ CONFIG: Detect').first().json.table_chat_histories_ru }}
    WHERE session_id = '{{ $json.sessionId }}' AND created_at >= NOW() - INTERVAL '20 days'
    UNION ALL
    SELECT id, session_id, message::json->>'type' as message_type, message::json->>'content' as content, created_at as timestamp, platform, 'en' as language
    FROM {{ $('âš™ï¸ CONFIG: Detect').first().json.table_chat_histories_en }}
    WHERE session_id = '{{ $json.sessionId }}' AND created_at >= NOW() - INTERVAL '20 days'
)
SELECT * FROM all_chats ORDER BY timestamp ASC;

