// ğŸ“ Format dialog for AI analysis
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const dialogs = items;
const mergedData = $('ğŸ”„ Merge with Language').first().json;
const analysisLanguage = mergedData.analysisLanguage || 'en';

const sessionData = {
    messages: [],
    language: mergedData.language,
    sessionId: mergedData.sessionId,
    messagesMap: {}
};

dialogs.forEach((item, index) => {
    const data = item.json;
    const orderNumber = index + 1;

    sessionData.messages.push({
        id: data.id,
        orderNumber: orderNumber,
        type: data.message_type,
        content: data.content,
        timestamp: data.timestamp,
        platform: data.platform
    });

    sessionData.messagesMap[orderNumber] = data.id;
});

const clientLabel = 'Client';
const botLabel = 'Bot';

const conversation = sessionData.messages.map(msg => 
    `[${msg.orderNumber}] ${msg.type === 'human' ? clientLabel : botLabel}: ${msg.content}`
).join('\n');

return [{
    json: {
        sessionId: mergedData.sessionId,
        userName: mergedData.userName,
        language: mergedData.language,
        analysisLanguage: analysisLanguage,
        conversation: conversation,
        messages: sessionData.messages,
        messagesMap: sessionData.messagesMap,
        totalMessages: sessionData.messages.length
    }
}];

