const aiResponse = items[0].json.output;
const sessionData = $('ğŸ“ Format Dialog for AI').first().json;
const messagesMap = sessionData.messagesMap || {};

let parsed;
try {
    let cleanJson = aiResponse.trim();
    cleanJson = cleanJson.replace(/^```json\s*/i, '').replace(/^```\s*/i, '').replace(/\s*```$/i, '');
    parsed = JSON.parse(cleanJson);

    if (!parsed.highlights || !Array.isArray(parsed.highlights)) {
        throw new Error('Invalid response structure');
    }
} catch (error) {
    return [{
        json: {
            hasHighlights: false,
            sessionId: sessionData.sessionId,
            totalDetected: 0,
            highlights: [],
            error: 'Failed to parse AI response'
        }
    }];
}

const validHighlights = parsed.highlights.filter(h => h.confidence >= 0.70);

const enrichedHighlights = validHighlights.map(h => {
    const orderNumber = h.messageId;
    const realMessageId = messagesMap[orderNumber];
    const message = sessionData.messages.find(m => m.id === realMessageId);

    return {
        sessionId: sessionData.sessionId,
        messageId: realMessageId,
        messageText: h.messageText || message?.content || '',
        messageType: message?.type || 'human',
        messageTimestamp: h.messageTimestamp || message?.timestamp,
        type: h.type,
        confidence: h.confidence,
        detection_method: 'ai',
        reasoning: h.reasoning,
        matched_patterns: h.matchedPatterns || [],
        language: sessionData.language,
        platform: message?.platform || 'unknown'
    };
});

return [{
    json: {
        hasHighlights: enrichedHighlights.length > 0,
        sessionId: sessionData.sessionId,
        totalDetected: enrichedHighlights.length,
        highlights: enrichedHighlights,
        summary: parsed.summary || {},
        aiAssessment: parsed.summary?.overallAssessment || ''
    }
}];

