  let response;
  const rawData = items[0].json;

  if (typeof rawData === 'string') {
    try {
      response = JSON.parse(rawData);
    } catch (e) {
      response = { 
        status: 'completed', 
        message: rawData,
        timestamp: new Date().toISOString()
      };
    }
  } else if (rawData.data && typeof rawData.data === 'string') {
    try {
      response = JSON.parse(rawData.data);
    } catch (e) {
      response = { 
        status: 'completed', 
        message: rawData.data,
        timestamp: new Date().toISOString()
      };
    }
  } else {
    response = rawData;
  }

