    throw new Error('–ù–µ—Ç –ø–æ–ª–µ–π –¥–ª—è –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è');
}

const query = `
UPDATE ${config.table_auth_users}
SET ${setClauses.join(', ')}
WHERE id = '${userId}'
RETURNING id, username, email, role, full_name, is_active;
`;

return [{
