WITH message_counts AS (
    SELECT 
        session_id,
        COUNT(*) as real_messages
    FROM (
        SELECT session_id FROM {{ $('âš™ï¸ CONFIG - Count').item.json.table_chat_ru }}
        UNION ALL
        SELECT session_id FROM {{ $('âš™ï¸ CONFIG - Count').item.json.table_chat_en }}
    ) all_messages
    GROUP BY session_id
)
SELECT 
    wm.session_id,
    COALESCE(mc.real_messages, 0) as message_count
FROM {{ $('âš™ï¸ CONFIG - Count').item.json.table_monitoring }} wm
LEFT JOIN message_counts mc ON wm.session_id = mc.session_id
WHERE wm.session_id IN (
    SELECT DISTINCT session_id 
    FROM {{ $('âš™ï¸ CONFIG - Count').item.json.table_monitoring }} 
    WHERE last_activity_time > NOW() - INTERVAL '24 hours'
);

