  return [{ json: { ...inputData, authorized: false, error: { success: false, error: 'Invalid API key', status: 401 } } }];
}

return [{ json: { ...inputData, authorized: true } }];

const contacts = {};

items.forEach(item => {
    const data = item.json;
    let messengers = [];

    if (data.telegram) messengers.push(`TG: ${data.telegram}`);
    if (data.whatsapp) messengers.push(`WA: ${data.whatsapp}`);
    if (data.instagram) messengers.push(`IG: ${data.instagram}`);

    if (data.other_contacts) {
        try {
            const other = typeof data.other_contacts === 'string' 
                ? JSON.parse(data.other_contacts) 
                : data.other_contacts;

            Object.entries(other).forEach(([platform, contact]) => {
                if (platform.startsWith('email') && data.email) {
                    if (contact !== data.email) messengers.push(`${platform}: ${contact}`);
                } else if (contact) {
                    messengers.push(`${platform}: ${contact}`);
                }
            });
        } catch (e) {}
    }

    contacts[data.session_id] = {
        name: data.full_name || `${data.first_name || ''} ${data.last_name || ''}`.trim() || null,
        firstName: data.first_name,
        lastName: data.last_name,
        phone: data.phone || data.phone_raw,
        email: data.email,
        messengers: messengers.join(', ') || null,
        telegram: data.telegram,
        whatsapp: data.whatsapp,
        instagram: data.instagram,
        company: data.company,
        position: data.position,
        location: data.location,
        preferredContact: data.preferred_contact,
        confidence: data.confidence_score,
        extractedFrom: data.extracted_from,
        lastUpdated: data.last_updated
    };
});

return [{json: {contacts}}];
